INSERT INTO `tbl_bbs` (`bbs_idx`, `bbs_title`, `category_code`, `member_id`, `fileYN`, `bbs_contents`, `reg_date`, `read_cnt`, `modify_date`) VALUES
	(111, '수업에 필요한 교사용 자료(DVD, PPT 등)는 어디에서 다운받을 수 있나요?', 'bc03', 'admin', NULL, '<p><span style="color: rgb(27, 27, 27); font-family: Roboto, &quot;Noto Sans KR&quot;, &quot;Malgun Gothic&quot;, Verdana, Dotum, AppleGothic, sans-serif; font-size: 13px;">교사용 자료 이용을 원하시는 선생님들 위해 무료 온라인 교수 학습 지원 사이트인 T셀파(tsherpa.co.kr)를 운영하고 있습니다.</span><br style="color: rgb(27, 27, 27); font-family: Roboto, &quot;Noto Sans KR&quot;, &quot;Malgun Gothic&quot;, Verdana, Dotum, AppleGothic, sans-serif; font-size: 13px;"><br style="color: rgb(27, 27, 27); font-family: Roboto, &quot;Noto Sans KR&quot;, &quot;Malgun Gothic&quot;, Verdana, Dotum, AppleGothic, sans-serif; font-size: 13px;"><span style="color: rgb(27, 27, 27); font-family: Roboto, &quot;Noto Sans KR&quot;, &quot;Malgun Gothic&quot;, Verdana, Dotum, AppleGothic, sans-serif; font-size: 13px;">T셀파 사이트에서 별도로 회원 가입을 하신 후, GPKI 인증을 하시면 다양한 자료를 이용하실 수 있습니다.</span></p>', '2024-05-05 22:22:50', 0, NULL),
	(112, '학습 교재(정오표, 정답 및 해설 등)의 관련 자료를 다운받고 싶어요.', 'bc03', 'admin', NULL, '<p><span style="font-family: Roboto, &quot;Noto Sans KR&quot;, &quot;Malgun Gothic&quot;, Verdana, Dotum, AppleGothic, sans-serif; font-size: 13px;"><span style="font-weight: bolder;">고객만족센터 &gt; 자료실</span></span><span style="color: rgb(27, 27, 27); font-family: Roboto, &quot;Noto Sans KR&quot;, &quot;Malgun Gothic&quot;, Verdana, Dotum, AppleGothic, sans-serif; font-size: 13px;">에서 학습 교재 관련 자료를 다운받을 수 있습니다.</span></p>', '2024-05-05 22:23:04', 0, NULL),
	(113, '교재에 대한 문의 사항이나 의견이 있어요.', 'bc03', 'admin', NULL, '<p><span style="font-family: Roboto, &quot;Noto Sans KR&quot;, &quot;Malgun Gothic&quot;, Verdana, Dotum, AppleGothic, sans-serif; font-size: 13px;"><span style="font-weight: bolder;">1:1 문의</span></span><span style="color: rgb(27, 27, 27); font-family: Roboto, &quot;Noto Sans KR&quot;, &quot;Malgun Gothic&quot;, Verdana, Dotum, AppleGothic, sans-serif; font-size: 13px;">에서 문의 사항을 남겨 주시면 과목별 담당자의 답변을 받아보실 수 있습니다.</span></p>', '2024-05-05 22:23:23', 0, NULL),
	(114, '자습서, 평가문제집을 구매하고 싶어요.', 'bc03', 'admin', NULL, '<p style="margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 1.5; color: rgb(27, 27, 27); font-family: Roboto, &quot;Noto Sans KR&quot;, &quot;Malgun Gothic&quot;, Verdana, Dotum, AppleGothic, sans-serif; font-size: 13px;">자습서와 평가문제집은 온라인에서 판매하지 않습니다.</p><p style="margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); line-height: 1.5; color: rgb(27, 27, 27); font-family: Roboto, &quot;Noto Sans KR&quot;, &quot;Malgun Gothic&quot;, Verdana, Dotum, AppleGothic, sans-serif; font-size: 13px;">주변의 가까운 서점에서 구매하시기 바랍니다.</p>', '2024-05-05 22:23:58', 0, NULL),
