mapper.select:
SELECT bbs.bbs_idx, bbs_title, bbs.category_code, bbs.member_id, bbs.fileYN, bbs_contents, bbs.reg_date, bbs.read_cnt, bbs.modify_date
FROM tbl_bbs AS bbs
WHERE bbs.bbs_idx = #{bbs_idx}

SELECT bbs_idx, bbs_title, category_code, fileYN, reg_date, modify_date
FROM tbl_bbs
WHERE bbs_idx > #{bbs_idx} AND category_code = #{category_code}

mapper.update:
UPDATE tbl_bbs SET
    bbs_title = #{bbs_title}
    , modify_date = NOW()
    , fileYN = #{fileYN}
    , bbs_contents = #{bbs_contents}
WHERE bbs_idx = #{bbs_idx}
