mapper.select:
SELECT bbs.bbs_idx, bbs_title, bbs.category_code, member_id, fileYN, replyYN, bbs_contents, bbs.reg_date, read_cnt

FROM tbl_bbs AS bbs


WHERE bbs.bbs_idx = #{idx}

mapper.update:
UPDATE tbl_bbs SET
    bbs_title = #{bbs_title}
    , reg_date = #{reg_date}
    , fileYN = #{fileYN}
    , bbs_contents = #{bbs_contents}
WHERE bbs_idx = #{bbs_idx}
