SELECT order_idx,order_num,member_id,order_date,cancel_YN,cancel_date,total_price,order_state,order_refund_request,order_refund_response FROM tbl_order
member_id=#{member_id} and cancel_YN = 'N'
AND order_date >= #{search_date1}
AND order_date < #{search_date2} + INTERVAL 1 DAY
ORDER BY order_idx DESC LIMIT #{page_skip_count}, #{page_size}

select payment_idx,payment_num,member_id,price,method,company,reg_date,use_type,title
from tbl_payment

member_id=#{member_id}


            AND reg_date >= #{search_date1}


            AND reg_date < #{search_date2} + INTERVAL 1 DAY



ORDER BY payment_idx DESC
LIMIT #{page_skip_count}, #{page_size}
