where.if:
AND ref_idx in (
    SELECT ref_idx
    FROM tbl_qna
    WHERE member_id = #{member_id}
)

mapper.update:
UPDATE tbl_qna SET
    read_cnt = read_cnt + 1
WHERE qna_idx = #{idx}

UPDATE tbl_qna SET
    ref_idx = #{qna_idx}
WHERE qna_idx = #{qna_idx} AND answerYN = 'N'

UPDATE tbl_qna SET
    title = #{title}
    , modify_date = NOW()

    , contents = #{contents}
WHERE qna_idx = #{qna_idx}
