mapper.select:
SELECT qna_idx, title, contents, member_id, answerYN, category_code, read_cnt, ref_idx, reg_date, modify_date
FROM tbl_qna
WHERE qna_idx = #{idx}

mapper.update:
UPDATE tbl_qna SET
    ref_idx = #{qna_idx}
WHERE qna_idx = #{qna_idx}

UPDATE tbl_bbs SET
    bbs_title = #{bbs_title}
    , reg_date = #{reg_date}
    , fileYN = #{fileYN}
    , bbs_contents = #{bbs_contents}
WHERE bbs_idx = #{bbs_idx}
