mapper.update:
UPDATE tbl_book
set rank_avg = (
SELECT AVG(rank)
FROM tbl_review
WHERE book_code = #{book_code}
GROUP BY book_code
)
WHERE book_code = #{book_code}

mapper.select:
SELECT review_idx, tr.member_id, book_code, tr.reg_date, rank, review_contents, profile, tm.member_name
FROM tbl_review as tr inner join tbl_member as tm
ON tr.member_id = tm.member_id
WHERE book_code = #{book_code}
ORDER BY review_idx DESC
LIMIT #{page_skip_count}, #{page_size}
